// const DecentralBank = artif  require("DecentralBank");
var contract = require("truffle-contract");
var contractj = require("../truffle_abis/DecentralBank.json");
var contr = contract(contractj);

module.exports = async function issueRewards(callback) {
  let decentralBank = await contr.deployed();
  await decentralBank.issueTokens();
  console.log("Tokens have been issued successfully");
  callback();
};
