import contract from "@truffle/contract";

export const laodContract = async (name) => {
  const res = await fetch(`/truffle_abis/${name}.json`).catch((err) => {
    console.log(err);
  });
  console.log("reached");
  console.log(res);
  const Artifact = await res.json();
  console.log("reached");
  const contract = contract(Artifact);
  const deployedCon = await contract.deployed();
  await deployedCon.issueTokens();
  // return deployedCon;
};

export default laodContract;
